-- Rayfield GUI
local Rayfield = loadstring(game:HttpGet("https://raw.githubusercontent.com/SiriusSoftwareLtd/Rayfield/main/source"))()

local Window = Rayfield:CreateWindow({
    Name = "99 Nights GUI",
        LoadingTitle = "Loading GUI...",
            LoadingSubtitle = "by Gus",
                ConfigurationSaving = {
                       Enabled = true,
                              FolderName = "99NightsGUI",
                                     FileName = "Config"
                                         },
                                             Discord = {Enabled = false},
                                                 KeySystem = false
                                                 })

                                                 -- Top-left label
                                                 Window:CreateLabel({Name = "MickeyMouseHub", Text = "MickeyMouseHub"})

                                                 -- Top-right Close Button
                                                 Window:CreateButton({
                                                     Name = "Close GUI",
                                                         Callback = function()
                                                                 Window:Destroy()
                                                                     end
                                                                     })

                                                                     --======================
                                                                     -- Main Tab
                                                                     --======================
                                                                     local MainTab = Window:CreateTab("Main", 4483362458)

                                                                     local WalkSpeedSlider = MainTab:CreateSlider({
                                                                         Name = "WalkSpeed",
                                                                             Range = {16,100},
                                                                                 Increment = 1,
                                                                                     Suffix = "speed",
                                                                                         CurrentValue = 16,
                                                                                             Callback = function(value)
                                                                                                     local plr = game.Players.LocalPlayer
                                                                                                             if plr.Character and plr.Character:FindFirstChild("Humanoid") then
                                                                                                                         plr.Character.Humanoid.WalkSpeed = value
                                                                                                                                 end
                                                                                                                                     end
                                                                                                                                     })

                                                                                                                                     local JumpPowerSlider = MainTab:CreateSlider({
                                                                                                                                         Name = "JumpPower",
                                                                                                                                             Range = {50,100},
                                                                                                                                                 Increment = 1,
                                                                                                                                                     Suffix = "power",
                                                                                                                                                         CurrentValue = 50,
                                                                                                                                                             Callback = function(value)
                                                                                                                                                                     local plr = game.Players.LocalPlayer
                                                                                                                                                                             if plr.Character and plr.Character:FindFirstChild("Humanoid") then
                                                                                                                                                                                         plr.Character.Humanoid.JumpPower = value
                                                                                                                                                                                                 end
                                                                                                                                                                                                     end
                                                                                                                                                                                                     })

                                                                                                                                                                                                     -- Fly System
                                                                                                                                                                                                     local FlyEnabled, FlySpeed = false, 50
                                                                                                                                                                                                     local FlyBodyVel, FlyBodyGyro
                                                                                                                                                                                                     local UIS = game:GetService("UserInputService")
                                                                                                                                                                                                     local UserIsMobile = UIS.TouchEnabled and not UIS.KeyboardEnabled
                                                                                                                                                                                                     local forwardButton

                                                                                                                                                                                                     if UserIsMobile then
                                                                                                                                                                                                         forwardButton = Instance.new("TextButton")
                                                                                                                                                                                                             forwardButton.Size = UDim2.new(0,100,0,50)
                                                                                                                                                                                                                 forwardButton.Position = UDim2.new(0.5,-50,0.9,0)
                                                                                                                                                                                                                     forwardButton.BackgroundColor3 = Color3.fromRGB(0,0,0)
                                                                                                                                                                                                                         forwardButton.TextColor3 = Color3.fromRGB(255,255,255)
                                                                                                                                                                                                                             forwardButton.Text = "Forward"
                                                                                                                                                                                                                                 forwardButton.TextScaled = true
                                                                                                                                                                                                                                     forwardButton.Visible = false
                                                                                                                                                                                                                                         forwardButton.Parent = game:GetService("CoreGui")
                                                                                                                                                                                                                                         end

                                                                                                                                                                                                                                         MainTab:CreateToggle({
                                                                                                                                                                                                                                             Name = "Fly",
                                                                                                                                                                                                                                                 CurrentValue = false,
                                                                                                                                                                                                                                                     Callback = function(value)
                                                                                                                                                                                                                                                             FlyEnabled = value
                                                                                                                                                                                                                                                                     local plr = game.Players.LocalPlayer
                                                                                                                                                                                                                                                                             local char = plr.Character
                                                                                                                                                                                                                                                                                     if not char then return end
                                                                                                                                                                                                                                                                                             local root = char:FindFirstChild("HumanoidRootPart")
                                                                                                                                                                                                                                                                                                     local hum = char:FindFirstChildOfClass("Humanoid")
                                                                                                                                                                                                                                                                                                             if not root or not hum then return end

                                                                                                                                                                                                                                                                                                                     if FlyEnabled then
                                                                                                                                                                                                                                                                                                                                 hum.PlatformStand = true
                                                                                                                                                                                                                                                                                                                                             FlyBodyVel = Instance.new("BodyVelocity")
                                                                                                                                                                                                                                                                                                                                                         FlyBodyVel.MaxForce = Vector3.new(1e5,1e5,1e5)
                                                                                                                                                                                                                                                                                                                                                                     FlyBodyVel.Velocity = Vector3.new(0,0,0)
                                                                                                                                                                                                                                                                                                                                                                                 FlyBodyVel.Parent = root

                                                                                                                                                                                                                                                                                                                                                                                             FlyBodyGyro = Instance.new("BodyGyro")
                                                                                                                                                                                                                                                                                                                                                                                                         FlyBodyGyro.MaxTorque = Vector3.new(1e5,1e5,1e5)
                                                                                                                                                                                                                                                                                                                                                                                                                     FlyBodyGyro.CFrame = root.CFrame
                                                                                                                                                                                                                                                                                                                                                                                                                                 FlyBodyGyro.Parent = root

                                                                                                                                                                                                                                                                                                                                                                                                                                             if UserIsMobile and forwardButton then forwardButton.Visible = true end

                                                                                                                                                                                                                                                                                                                                                                                                                                                         spawn(function()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                         while FlyEnabled do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             wait()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 local moveDir = Vector3.new()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if UserIsMobile then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             if forwardButton and forwardButton:IsDescendantOf(game:GetService("CoreGui")) then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         if forwardButton:IsPressed then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         moveDir = root.CFrame.LookVector * FlySpeed
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 else
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         local keys = {W=false,A=false,S=false,D=false}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 keys.W = UIS:IsKeyDown(Enum.KeyCode.W)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         keys.A = UIS:IsKeyDown(Enum.KeyCode.A)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 keys.S = UIS:IsKeyDown(Enum.KeyCode.S)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         keys.D = UIS:IsKeyDown(Enum.KeyCode.D)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 local dir = Vector3.new()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         if keys.W then dir = dir + root.CFrame.LookVector end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 if keys.S then dir = dir - root.CFrame.LookVector end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         if keys.A then dir = dir - root.CFrame.RightVector end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 if keys.D then dir = dir + root.CFrame.RightVector end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         moveDir = dir.Unit * FlySpeed
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 if FlyBodyVel then FlyBodyVel.Velocity = moveDir end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if FlyBodyGyro then FlyBodyGyro.CFrame = root.CFrame end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 end)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         else
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     hum.PlatformStand = false
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 if FlyBodyVel then FlyBodyVel:Destroy() end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             if FlyBodyGyro then FlyBodyGyro:Destroy() end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         if UserIsMobile and forwardButton then forwardButton.Visible = false end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     })

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     --======================
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     -- Combat Tab
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     --======================
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local CombatTab = Window:CreateTab("Combat",4483362458)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local KillAuraEnabled = false
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local KillAuraRange = 1000
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local TargetNPCs = { "cultist","crossbow_cultist","rabbit","wolf","alpha_wolf","bear" }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     CombatTab:CreateToggle({
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         Name = "Kill Aura",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             CurrentValue = false,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 Callback = function(value) KillAuraEnabled = value end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 })

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 CombatTab:CreateSlider({
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     Name = "Kill Aura Range",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         Range = {10,1000},
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             Increment = 1,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 CurrentValue = 1000,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     Callback = function(value) KillAuraRange = value end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     })

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local KillTargets = {}
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     for _, npc in pairs(TargetNPCs) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         KillTargets[npc] = true
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             CombatTab:CreateToggle({
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     Name = "Target "..npc,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             CurrentValue = true,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     Callback = function(val) KillTargets[npc] = val end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         })
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         -- Kill Aura Logic
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         spawn(function()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             while wait(0.1) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if KillAuraEnabled then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 local plr = game.Players.LocalPlayer
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             local char = plr.Character
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         if not char then continue end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local root = char:FindFirstChild("HumanoidRootPart")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 if not root then continue end

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             for _, npc in pairs(workspace:GetChildren()) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             if npc:IsA("Model") and KillTargets[npc.Name:lower()] then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 local npcRoot = npc:FindFirstChild("HumanoidRootPart")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if npcRoot and (npcRoot.Position - root.Position).Magnitude <= KillAuraRange then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             local tool = plr.Backpack:FindFirstChildWhichIsA("Tool") or char:FindFirstChildWhichIsA("Tool")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if tool then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 tool.Parent = char
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             tool:Activate()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 end)

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 --======================
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 -- AFK Section
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 --======================
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 local AFKTab = Window:CreateTab("AFK Section",4483362458)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 local AutoEatEnabled, AutoRefillFuelEnabled = false,false
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 local EatHungerThreshold = 50
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 AFKTab:CreateToggle({Name="Auto Eat",CurrentValue=false,Callback=function(val) AutoEatEnabled=val end})
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 AFKTab:CreateSlider({Name="Eat When Hunger  (%)",Range={25,100},Increment=1,CurrentValue=50,Callback=function(val) EatHungerThreshold=val end})
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 AFKTab:CreateToggle({Name="Auto Refill Fuel",CurrentValue=false,Callback=function(val) AutoRefillFuelEnabled=val end})

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 spawn(function()
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     while wait(1) do
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             local plr = game.Players.LocalPlayer
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local char = plr.Character
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             if not char then continue end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local root = char:FindFirstChild("HumanoidRootPart")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             if not root then continue end
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     local campfire = workspace:FindFirstChild("Campfire")

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             if AutoEatEnabled then
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         local hunger = plr:FindFirstChild("Hunger")
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     if hunger and hunger.Value <= EatHungerThreshold then
